import React, { useEffect, useState, useCallback } from 'react';
import { useHistory } from 'react-router-dom'; // Retour à useHistory pour React Router v5
import { api } from '../services/api';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [editingUser, setEditingUser] = useState(null);
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        photo: null,
        preview: null
    });

    const history = useHistory(); // Utilisation de useHistory au lieu de useNavigate

    const fetchUsers = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                history.push('/login');
                return;
            }

            const response = await api.admin.getUsers();
            setUsers(response);
        } catch (err) {
            if (err.response?.status === 401) {
                localStorage.removeItem('token');
                history.push('/login');
            } else {
                setError('Error fetching users. Please try again.');
                console.error('Error:', err);
            }
        } finally {
            setLoading(false);
        }
    }, [history]);

    useEffect(() => {
        fetchUsers();
    }, [fetchUsers]);

    useEffect(() => {
        if (formData.photo) {
            const objectUrl = URL.createObjectURL(formData.photo);
            setFormData(prev => ({ ...prev, preview: objectUrl }));
            return () => URL.revokeObjectURL(objectUrl);
        }
    }, [formData.photo]);

    const handleDelete = async (id) => {
        if (!window.confirm('Are you sure you want to delete this user?')) {
            return;
        }

        try {
            await api.admin.deleteUser(id);
            setUsers(prev => prev.filter(user => user._id !== id));
        } catch (err) {
            if (err.response?.status === 401) {
                localStorage.removeItem('token');
                history.push('/login');
            } else {
                setError('Error deleting user. Please try again.');
                console.error('Error:', err);
            }
        }
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        const formDataToSend = new FormData();
        formDataToSend.append('name', formData.name);
        formDataToSend.append('email', formData.email);
        if (formData.photo) {
            formDataToSend.append('photo', formData.photo);
        }

        try {
            const response = await api.admin.updateUser(editingUser._id, formDataToSend);
            setUsers(prev => prev.map(user => 
                user._id === editingUser._id 
                    ? { ...user, name: formData.name, email: formData.email, photo: response.photo || user.photo } 
                    : user
            ));
            handleCancelEdit();
        } catch (err) {
            if (err.response?.status === 401) {
                localStorage.removeItem('token');
                history.push('/login');
            } else {
                setError('Error updating user. Please try again.');
                console.error('Error:', err);
            }
        } finally {
            setLoading(false);
        }
    };

    const handleEdit = (user) => {
        setEditingUser(user);
        setFormData({
            name: user.name,
            email: user.email,
            photo: null,
            preview: user.photo
        });
        setError(null);
    };

    const handleCancelEdit = () => {
        setEditingUser(null);
        setFormData({
            name: '',
            email: '',
            photo: null,
            preview: null
        });
        setError(null);
    };

    const handleInputChange = (e) => {
        const { name, value, files } = e.target;
        if (name === 'photo' && files) {
            setFormData(prev => ({
                ...prev,
                photo: files[0]
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    if (loading) {
        return <div className="admin-dashboard loading">Loading...</div>;
    }

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>
            {error && <div className="error" role="alert">{error}</div>}

            <div className="dashboard-content">
                <table className="admin-table" role="grid">
                    <thead>
                        <tr>
                            <th scope="col">Name</th>
                            <th scope="col">Email</th>
                            <th scope="col">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user._id}>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>
                                    <div className="action-buttons">
                                        <button 
                                            className="btn btn-edit" 
                                            onClick={() => handleEdit(user)}
                                            disabled={loading}
                                            aria-label={`Edit ${user.name}`}
                                        >
                                            Edit
                                        </button>
                                        <button 
                                            className="btn btn-delete" 
                                            onClick={() => handleDelete(user._id)}
                                            disabled={loading}
                                            aria-label={`Delete ${user.name}`}
                                        >
                                            Delete
                                        </button>
                                    </div>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                {editingUser && (
                    <form className="edit-form" onSubmit={handleUpdate} aria-label="Edit user form">
                        <div className="form-group">
                            <label htmlFor="name">Name</label>
                            <input 
                                id="name"
                                type="text" 
                                name="name"
                                value={formData.name} 
                                onChange={handleInputChange} 
                                required 
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input 
                                id="email"
                                type="email" 
                                name="email"
                                value={formData.email} 
                                onChange={handleInputChange} 
                                required 
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="photo">Photo</label>
                            <input 
                                id="photo"
                                type="file" 
                                name="photo"
                                onChange={handleInputChange} 
                                accept="image/*"
                            />
                            {formData.preview && (
                                <img 
                                    src={formData.preview} 
                                    alt="Preview" 
                                    className="photo-preview" 
                                />
                            )}
                        </div>
                        <div className="form-actions">
                            <button 
                                className="btn btn-edit" 
                                type="submit"
                                disabled={loading}
                            >
                                {loading ? 'Updating...' : 'Update'}
                            </button>
                            <button 
                                className="btn btn-cancel" 
                                type="button"
                                onClick={handleCancelEdit}
                                disabled={loading}
                            >
                                Cancel
                            </button>
                        </div>
                    </form>
                )}
            </div>
        </div>
    );
};

export default AdminDashboard;
