import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [editingUser, setEditingUser] = useState(null);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [photo, setPhoto] = useState(null);
    const [preview, setPreview] = useState(null);

    // Récupérer la liste des utilisateurs
    useEffect(() => {
        let isMounted = true;

        const fetchUsers = async () => {
            try {
                const response = await api.admin.getUsers();
                if (isMounted) {
                    setUsers(response);
                }
            } catch (err) {
                if (isMounted) {
                    setError('Error fetching users. Please try again.');
                    console.error('Error:', err);
                }
            } finally {
                if (isMounted) {
                    setLoading(false);
                }
            }
        };

        fetchUsers();

        return () => {
            isMounted = false;
        };
    }, []);

    // Mettre à jour la preview de la photo sélectionnée
    useEffect(() => {
        if (photo) {
            const objectUrl = URL.createObjectURL(photo);
            setPreview(objectUrl);

            return () => URL.revokeObjectURL(objectUrl);
        }
    }, [photo]);

    // Suppression d'un utilisateur
    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            try {
                await api.admin.deleteUser(id);
                setUsers(users.filter(user => user._id !== id));
            } catch (err) {
                setError('Error deleting user. Please try again.');
                console.error('Error:', err);
            }
        }
    };

    // Mise à jour d'un utilisateur
    const handleUpdate = async (e) => {
        e.preventDefault();
        setLoading(true);

        const formData = new FormData();
        formData.append('name', name);
        formData.append('email', email);
        if (photo) {
            formData.append('photo', photo);
        }

        try {
            await api.admin.updateUser(editingUser._id, formData);
            setUsers(users.map(user => (user._id === editingUser._id ? { ...user, name, email, photo: preview } : user)));
            setEditingUser(null);
            setName('');
            setEmail('');
            setPhoto(null);
            setPreview(null);
        } catch (err) {
            setError('Error updating user. Please try again.');
            console.error('Error:', err);
        } finally {
            setLoading(false);
        }
    };

    // Sélectionner un utilisateur pour l'édition
    const handleEdit = (user) => {
        setEditingUser(user);
        setName(user.name);
        setEmail(user.email);
        setPreview(user.photo);
    };

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>
            {error && <div className="error">{error}</div>}
            {loading ? (
                <div>Loading...</div>
            ) : (
                <div>
                    <table>
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <tr key={user._id}>
                                    <td>{user.name}</td>
                                    <td>{user.email}</td>
                                    <td>
                                        <button onClick={() => handleEdit(user)}>Edit</button>
                                        <button onClick={() => handleDelete(user._id)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    {editingUser && (
                        <form onSubmit={handleUpdate}>
                            <div>
                                <label>Name</label>
                                <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
                            </div>
                            <div>
                                <label>Email</label>
                                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                            </div>
                            <div>
                                <label>Photo</label>
                                <input type="file" onChange={(e) => setPhoto(e.target.files[0])} />
                                {preview && <img src={preview} alt="Preview" className="photo-preview" />}
                            </div>
                            <button type="submit">Update</button>
                        </form>
                    )}
                </div>
            )}
        </div>
    );
};

export default AdminDashboard;
